<style scoped lang="scss">
.popup {
  z-index: 1000;
  position: fixed;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;

  display: flex;
  justify-content: center;
  align-items: center;
  padding: 1rem;

  &__container {
    z-index: 1;
    border-radius: 0.5rem;
    background-color: #fff;
    width: 80ch;
    max-width: 100%;
    padding: 3rem 5rem;
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1),
      0 10px 10px -5px rgba(0, 0, 0, 0.04);
    display: flex;
    align-items: center;
  }

  &__title {
    font-size: 2rem;
    font-weight: 900;
    color: #111828;
    margin: 0.75rem 0 0;
  }
  &__subtitle {
    color: #6466f1;
    font-weight: 800;
    font-size: 3rem;
  }
  &__text {
    margin: 2rem 0 0;
    color: #4a4a53;
    font-size: 1.125rem;
    line-height: 1.75;
  }
  &__accept-button {
    margin: 2rem 0 0;
    display: inline-block;
    padding: 1rem 1.5rem;
    text-align: center;
    border: none;
    border-radius: 0.5rem;
    color: #fff;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.25rem;
    box-shadow: 0px 2px 8px #4f46e480, 0 0 0 0 #fff, 0 0 0 0 #4a4a53;
    cursor: pointer;
    z-index: 1;
    outline: none;
    transition: box-shadow 200ms cubic-bezier(0.18, 0.89, 0.32, 1.28);
    appearance: none;
    position: relative;
    background: #4f46e4 linear-gradient(45deg, #4776e6, #8e54e9);

    &:focus-visible {
      box-shadow: 0px 2px 8px #4f46e480, 0 0 0 0.125rem #fff,
        0 0 0 0.35rem #4a4a53;
      outline: none;
    }
  }
  &__content {
    &--left {
      flex-grow: 1;
    }
    // &--right {
    // }
  }

  &__image {
    margin-left: 3rem;
    .path {
      stroke: url(#line-gradient-welcome);
      stroke-width: 5;
      fill: none;
      stroke-linecap: round;
      stroke-linejoin: round;
      transition: clip-path 500ms linear;
    }
  }
}
.background {
  position: absolute;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.15);
  backdrop-filter: blur(5px);
  background-image: url("data:image/svg+xml,%3Csvg width='52' height='26' viewBox='0 0 52 26' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='rgba(255, 255, 255, 0.4)' fill-opacity='0.4'%3E%3Cpath d='M10 10c0-2.21-1.79-4-4-4-3.314 0-6-2.686-6-6h2c0 2.21 1.79 4 4 4 3.314 0 6 2.686 6 6 0 2.21 1.79 4 4 4 3.314 0 6 2.686 6 6 0 2.21 1.79 4 4 4v2c-3.314 0-6-2.686-6-6 0-2.21-1.79-4-4-4-3.314 0-6-2.686-6-6zm25.464-1.95l8.486 8.486-1.414 1.414-8.486-8.486 1.414-1.414z' /%3E%3C/g%3E%3C/g%3E%3C/svg%3E");
}

.fade-enter-active,
.fade-leave-active {
  transition: opacity 500ms cubic-bezier(0.22, 1, 0.36, 1);

  .background {
    transition: inherit;
  }

  .popup__container {
    transition: opacity 500ms cubic-bezier(0.33, 1, 0.68, 1),
      transform 500ms cubic-bezier(0.33, 1, 0.68, 1);
  }
}
.fade-leave-active {
  pointer-events: none;
}

.fade-enter-from {
  .background {
    opacity: 0;
  }
  .popup__container {
    opacity: 0;
    transform: translateY(2rem);
  }
}
.fade-leave-to {
  .background {
    opacity: 0;
  }
  .popup__container {
    opacity: 0;
    transform: translateY(-2rem);
  }
}
</style>
